mapper.insert:
insert into members (
        name
        , mail
        , password
        , phone_number
        , role
) values (
        #{name}
        , #{mail}
        , #{password}
        , #{phoneNumber}
        , #{role}
)

mapper.update:
update  members
set
        name = #{name}
        , mail = #{mail}
        , password = #{password}
        , phone_number = #{phoneNumber}
        , role = #{role}
where
        id = #{id}

update  members
set
        password = #{encodedPassword}
